import React, {Component} from 'react';
import './App.css';
import axios from 'axios';




class Form extends Component {
  state = {
    userInput: ''
  }

  handleSubmit = async (event) => {
      event.preventDefault();
      try {
        const response = await this.props.axiosFetchProfile(`https://api.github.com/users/${this.state.userInput}`);
        console.log(response);
        this.props.onSubmit(response.data);
        this.setState({ userInput: ''});
      } catch(error) {
          alert('wrong user name, try again!');
          this.setState({ userInput: ''}); 
      }
    }

  handleSearchChange = (event) => {
    console.log(event)
    // event => this.setState({ userInput: event.target.value})
  }

  render() {
    return(
        <form onSubmit={this.handleSubmit}>
          <input 
            type='text'
            value={this.state.userInput}
            onChange={this.handleSearchChange}
            placeholder='enter username'
            required
          />
          <button>add user</button>
        </form>

    )
  }
}  

class Card extends Component {

  render() {
    const userInfo = this.props;
    return(
      <div className='card'>
        <img src={userInfo.avatar_url} alt='profile'/>
        <div className='info'>
          <div className ='name'>{userInfo.name}</div>
          <div className ='company'>{userInfo.company}</div>
        </div>
      </div>
    )
  }
}

class Cardlist extends Component {

  render() {
    const profiles = this.props.profiles;
    return(
      <div className='cardlist'>
        {profiles.map(profile => <Card key={profile.id} {...profile} />)}       
      </div>
    )
  }
}

class App extends Component {
  state = {
    profiles: []
  }
  addNewProfile = (profileData) => {
    this.setState(prevState => ({
      profiles: [...prevState.profiles, profileData]
    }));
  }
  axiosFetchProfile = async (url) => {
        const response = await axios.get(url);
        return response;
  }

  render() {
    // console.log(this.state.profiles);
    return(
      <div>
        <h1>github profile cards</h1>
        <Form onSubmit={this.addNewProfile} axiosFetchProfile={this.axiosFetchProfile}/>
        <Cardlist profiles={this.state.profiles}/>
      </div>
    )
  }
}

export default App;
