import React, {Component} from 'react';
import './App.css';
import axios from 'axios';


const testData = [
			{name: "Dan Abramov", avatar_url: "https://avatars0.githubusercontent.com/u/810438?v=4", company: "@facebook"},
      {name: "Sophie Alpert", avatar_url: "https://avatars2.githubusercontent.com/u/6820?v=4", company: "Humu"},
  		{name: "Sebastian Markbåge", avatar_url: "https://avatars2.githubusercontent.com/u/63648?v=4", company: "Facebook"},
  ];


class Form extends Component {
  state = {
    userInput: ''
  }

  profiles = this.props.profiles;

  handleSubmit = async (event) => {
      event.preventDefault();
      const response = await axios.get(`https://api.github.com/users/${this.state.userInput}`);
      console.log(this.profiles);
    }

  render() {
    return(
        <form onSubmit={this.handleSubmit}>
          <input 
            type='text'
            value={this.state.userInput}
            onChange={event => this.setState({ userInput: event.target.value})}
            placeholder='enter username'
            required
          />
          <button>add user</button>
        </form>

    )
  }
}  

class Card extends Component {

  render() {
    const userInfo = this.props;
    return(
      <div className='card'>
        <img src={userInfo.avatar_url} alt='profile'/>
        <div className='info'>
          <div className ='name'>{userInfo.name}</div>
          <div className ='company'>{userInfo.company}</div>
        </div>
      </div>
    )
  }
}

class Cardlist extends Component {

  render() {
    const profiles = this.props.profiles;
    return(
      <div className='cardlist'>
        {profiles.map(profile => <Card {...profile} />)}       
      </div>
    )
  }
}

class App extends Component {
  state = {
    profiles: testData
  }
  render() {
    // console.log(this.state.profiles);
    return(
      <div>
        <h1>github profile cards</h1>
        <Form profiles={this.state.profiles}/>
        <Cardlist profiles={this.state.profiles}/>
      </div>
    )
  }
}

export default App;
