const Card = (props) => {
  return(
    <div className='card'>
         <img src={props.avatar_url} alt='profile'/>
         <div className='info'>
           <div className ='name'>{props.name}</div>
           <div className ='company'>{props.company}</div>
         </div>
       </div>
  );
}
class Cardlist extends Component {

  render() {
    const profiles = this.props.profiles;
    return(
      <div className='cardlist'>
        {profiles.map(profile => <Card key={profile.id} {...profile} />)}       
      </div>
    )
  }
}

class App extends Component {
  state = {
    profiles: []
  }
  addNewProfile = (profileData) => {
    this.setState(prevState => ({
      profiles: [...prevState.profiles, profileData]
    }));
  }
  axiosFetchProfile = async (url) => {
        const response = await axios.get(url);
        return response;
  }

  render() {
    return(
      <div>
        <h1>github profile cards</h1>
        <Form onSubmit={this.addNewProfile} axiosFetchProfile={this.axiosFetchProfile}/>
        <Cardlist profiles={this.state.profiles}/>
      </div>
    )
  }
}

export default App;
